import React, { Component } from 'react'
import BuyForm from './BuyForm'
import SellForm from './SellForm'

class Main extends Component {
    constructor(props) {
      super(props)
      this.state = {
        currentForm: 'sell'
      }
    }

    render() {
        let content
        if (this.state.currentForm === "buy"){
            content = <BuyForm ethBalance={this.props.ethBalance} tokenBalance={this.props.tokenBalance} buyTokens={this.props.buyTokens}/>
        } else {
            content = <SellForm ethBalance={this.props.ethBalance} tokenBalance={this.props.tokenBalance} buyTokens={this.props.buyTokens}/>
        }
      return (
        <div id="content">

<div className="d-flex justify-content-between mb-3">
          <button
              className="btn btn-light"
              onClick={(event) => {
                this.setState({ currentForm: 'buy' })
              }}
            >
            Buy
          </button>
          <span className="text-muted">&lt; &nbsp; &gt;</span>
          <button
              className="btn btn-light"
              onClick={(event) => {
                this.setState({ currentForm: 'sell' })
              }}
            >
            Sell
          </button>
        </div>

          <div className="card mb-4" >

            <div className="card-body">

                {content}

            </div>

          </div>

        </div>
      );
    }
  }

  export default Main;
